import React from 'react'
import * as api from '../Api'
import Slider from '../components/Slider/Slider'
import ServicesList from '../components/Services/ServicesList'
import ServicesNav from '../components/Slider/Slider'

function Services({}) {

    //onClick={isMenuCliked}
    //isMenuCliked, onCardClick

    const [isMobile, setIsMobile] = React.useState(false)
    const [services, setServices] = React.useState({})
    const [category, setCategory] = React.useState({})

    const handleResize = () => {
      if (window.innerWidth < 1100) {
        setIsMobile(true)
      } else {
        setIsMobile(false)
      }
    }

    React.useEffect(() => {
      window.addEventListener("resize", handleResize)
    })

    React.useEffect(() => {
      getServices()
    }, [])

    const getServices = () => {
      api.getServices({
        })
        .then(res => {
          getServices(res)
        })
    }  
/*
    React.useEffect(() => {
      getEquipmentCategory()
    }, [])

    const getEquipmentCategory = () => {
      api.getEquipmentCategory()
        .then(res => {
          setCategory(res)
          console.log(res)
        })
    }
*/
    return (
        <>
            {isMobile?
            <>
            <div className='equipment__wrapper'>
                <h3 className='equipment__title'>Услуги</h3>
                <div className='quipment__menu'>
                    <p className='quipment__text'>Все товары</p>
                    <button className='equipment__menu-btn'></button>
                </div>
            </div>
            <Slider></Slider>
            <ServicesList
            data={services}
            />
            </>
            :
            <>
            <Slider></Slider>
            <h3 className='equipment__title-dop'>Услуги</h3>
            <div className='equipment__container'>
                <ServicesNav
                />
                <ServicesList
                data={services}
                />
            </div>
            </>
            }
        </>

    );
  }

  export default Services;
