import { Route, Routes, useNavigate, Navigate } from 'react-router-dom'
import React from 'react'
import * as api from './Api'
import './App.css'
import Header from './components/Header/Header'
import Footer from './components/Footer/Footer'
import Main from './pages/main'
import Equipment from './pages/equipment'
import MainNavigation from './components/Navigation/MainNavigation'
import EquipmentNavigation from './components/Navigation/EquipmentNavigation'
import PopupBag from './components/PopupBag/PopupBag'

function App() {
  const [isBurgerMenuOpen, setIsBurgerMenuOpen] = React.useState(false)
  const [isMenuOpen, setIsMenuOpen] = React.useState(false)
  const [isBagOpen, setIsBagOpen] = React.useState(false)
  const [selectedCard, setSelectedCard] = React.useState({})
  const [equipment, setEquipment] = React.useState({})

/*
  React.useEffect(() => {
    getEquipment()
  }, [])*/

  const getEquipment = () => {
    api.getEquipment({
      //is_popular = true,
      })
      .then(res => {
        /*console.log(res)*/
        setEquipment(res)
        console.log(equipment)
        /*setEquipment = res.equipment
        const { count } = res
        setOrders(count)*/
      })
  }  

/*
  const getProducts = () => {
    api.getProducts({
      //is_popular = true,
      })
      .then(res => {
        console.log(res)
      })
  }*/

  function handleCardClick(card) {
    setSelectedCard(card.id);
  }

  function handleBurgerMenuClick() {
    setIsBurgerMenuOpen(true)
  }

  function handleMenuClick() {
    setIsMenuOpen(true)
  }

  function handleBagClick() {
    setIsBagOpen(true)
    console.log('click bag')
  }

  function closeAllPopups() {
    setIsBurgerMenuOpen(false)
    setIsMenuOpen(false)
    setIsBagOpen(false)
  }

  return (
    <div className="App">
      <Header
          isBurgerMenuCliked={handleBurgerMenuClick}
          isbagCliked={handleBagClick}
      />

      <Routes>

          <Route
              exact path="/"
              element={
                <Main
                getEquipment={getEquipment}/>
              }
          />

          <Route
              path="/equipment"
              element={
                <Equipment
                onCardClick={handleCardClick}
                isMenuCliked={handleMenuClick}
                data={equipment}/>
              }
          />

      </Routes>
      <MainNavigation
          isOpen={isBurgerMenuOpen}
          onClose={closeAllPopups} 
          isbagCliked={handleBagClick}
      />
      <EquipmentNavigation
          isOpen={isMenuOpen}
          onClose={closeAllPopups} 
      />
      <PopupBag
          onClose={closeAllPopups}
          isOpen={isBagOpen}
      />

      <Footer></Footer>
    </div>
  );
}

export default App;
