import './Slider.css'
import dataSlaider from './DataSlaider';
import React from "react"

function Slider() {
  const [slideIndex, setSlideIndex] = React.useState(1)

  const getNext = () => {
    if(slideIndex !== dataSlaider.length) {
      setSlideIndex(slideIndex + 1)
    } else if (slideIndex === dataSlaider.length) {
      setSlideIndex(1)
    }
  }

  const getPrev = () => {

  }
/*
  const moveDot = index => {
    setSlideIndex(index)
  }*/

  return (
      <div className="slider__container">
        {dataSlaider.map((item, index) => {
          return(

            <div key={item.id} className={slideIndex === index + 1 ? 'slide active-anim' : 'slide'}>
              <div className="offers__container">
                <div className="offers__text-wrapper">
                  <p className="offers__title">{item.title}</p>
                <div className="offers__text-container">
                  <p className="offers__text">{item.subtitle}</p>
                  <p className="offers__text">{item.text}</p>
                </div>
                </div>  
                <img className="offers__pic" src={item.picture}></img>
              </div>

              <div className='slide-container-btn'>
                <button onClick={getNext} className='slide-btn'></button>
                <button onClick={getPrev} className='slide-btn'></button>
              </div>


            </div>
          )
        })}
      </div>
  );
}

export default Slider;
