import './Slider.css'
import Offer1 from '../Offers/Offer1' 
import Offer2 from '../Offers/Offer2'
import FilterCheckbox from '../FilterCheckbox/FilterCheckbox'
import React from "react"

function Slider() {

  const slide = React.useRef(null)
  const [prev, setPrev] = React.useState(false)
  const [next, setNext] = React.useState(false)
/*
  setTimeout(() => {
    ;
  }, 5000);*/

  let position = 0

  function getNext() {
    if (position === 0) {
        setPrev(true)
    } else {
      position += 1000
      setPrev(false)
      slide.current.childNodes.forEach((item) => {
        item.style = `transform: translateX(${position})px`
      })  
    }
  }

  function getPrev() {
    slide.current.childNodes.forEach((item) => {
      item.style = "transform: translateX(-1000px)"
  })
  }


  return (
      <div className="slider__container">
        <div className="slider">
          <div className="slider__item" ref={slide}>
            <Offer1></Offer1>
            <Offer2></Offer2>
          </div>
        </div>
            <button onClick={getNext}>next</button>
            <button onClick={getPrev}>prev</button>
      </div>
  );
}

export default Slider;
